// Premium Login Component
const PremiumLogin: React.FC<{ onBack: () => void; onLogin: (user: User) => void }> = ({ onBack, onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      const data = await response.json();

      if (response.ok && data.token) {
        // Stocker les informations de connexion
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify({
          ...data.user,
          type: data.user.subscription_type === 'enterprise' ? 'admin' : 'user'
        }));

        // Appeler la fonction onLogin
        onLogin({
          id: data.user.id,
          email: data.user.email,
          type: data.user.subscription_type === 'enterprise' ? 'admin' : 'user',
          name: data.user.email,
          subscription_type: data.user.subscription_type
        });
      } else {
        setError(data.message || 'Email ou mot de passe incorrect');
      }
    } catch (error) {
      setError('Erreur de connexion. Veuillez rÃ©essayer.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '2rem'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '16px',
        boxShadow: '0 20px 60px rgba(0,0,0,0.2)',
        padding: '3rem',
        width: '100%',
        maxWidth: '420px',
        position: 'relative'
      }}>
